import { expect, it } from 'vitest'
import { getHighlighter } from 'shiki'
import { transformMagicMove } from '../packages/slidev/node/syntax/transform'
import { createTransformContext } from './_tutils'

it('basic', async () => {
  const code = `

Some text before

\`\`\`\`md magic-move
\`\`\`ts
// This is a code block
console.log('Hello, Slidev!')
\`\`\`
\`\`\`ts
let message = 'Hello, Slidev!'
\`\`\`
\`\`\`\`

Some text after
  
`
  const shiki = await getHighlighter({
    themes: ['nord'],
    langs: ['typescript'],
  })

  const ctx = createTransformContext(code)

  transformMagicMove(
    ctx,
    shiki,
    {
      theme: 'nord',
    },
  )

  expect(ctx.s.toString())
    .toMatchInlineSnapshot(`
      "

      Some text before

      <ShikiMagicMove v-bind="{}" steps-lz="" :step-ranges='[[],[]]' />

      Some text after
        
      "
    `)
})

it('hyphenated code language', async () => {
  const code = `

Some text before

\`\`\`\`md magic-move
\`\`\`angular-ts
console.log('Hello, Angular!')
\`\`\`
\`\`\`angular-ts
console.log('Hello, Angular #2!')
\`\`\`
\`\`\`\`

Some text after
  
`
  const shiki = await getHighlighter({
    themes: ['nord'],
    langs: ['angular-ts'],
  })

  const ctx = createTransformContext(code)

  transformMagicMove(
    ctx,
    shiki,
    {
      theme: 'nord',
    },
  )

  expect(ctx.s.toString())
    .toMatchInlineSnapshot(`
        "

        Some text before

        <ShikiMagicMove v-bind="{}" steps-lz="" :step-ranges='[[],[]]' />

        Some text after
          
        "
      `)
})
